glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform Sprite_Input {
  vec4 uvOffset;
  vec2 textureSize;
  float strengthFloat;
  float sharpnessFloat;
  float ignoreAlphaThreshold;
  float spriteRotated;
};
const int sampleRate = 32;
float uvOffsetLeft = uvOffset.x;
float uvOffsetTop = uvOffset.y;
float uvOffsetRight = uvOffset.z;
float uvOffsetBottom = uvOffset.w;
float textureWidth = textureSize.x;
float textureHeight = textureSize.y;
float getBorderTop () {
  if (spriteRotated > 0.5){
    return uvOffsetLeft;
  } else {
    return uvOffsetTop;
  }
}
float getBorderBottom () {
  if (spriteRotated > 0.5){
    return uvOffsetRight;
  } else {
    return uvOffsetBottom;
  }
}
float getTargetStregth () {
  if (spriteRotated > 0.5){
    return strengthFloat / float(textureWidth);
  } else {
    return strengthFloat / float(textureHeight);
  }
}
float getTargetUv0 (float rawUv0) {
  float borderTop = getBorderTop();
  float borderBottom = getBorderBottom();
  float range = borderBottom - borderTop;
  return (rawUv0 - (borderTop + getTargetStregth())) / (range - getTargetStregth() * 2.0) * (range) + (borderTop);
}
float getTargetPosition (int index) {
  if (spriteRotated > 0.5){
    return getTargetUv0(v_uv0.x + float(index) * strengthFloat / float(textureWidth) / float(sampleRate));
  } else {
    return getTargetUv0(v_uv0.y + float(index) * strengthFloat / float(textureHeight) / float(sampleRate));
  }
}
vec4 getTargetPixel (float targetPosition) {
  if (spriteRotated > 0.5){
    return texture2D(texture, vec2(targetPosition, v_uv0.y));
  } else {
    return texture2D(texture, vec2(v_uv0.x, targetPosition));
  }
}
void getNewPixel (int index, inout bool isEmpty, inout float sum, inout vec4 edgePixel, inout int edgeIndex, inout vec4 outputPixel) {
  float multiplier = exp(float(index) * float(index) / (-2.0 * sharpnessFloat * sharpnessFloat));
  float targetPosition = getTargetPosition(index);
  if (getBorderTop() <= targetPosition && targetPosition <= getBorderBottom()){
    vec4 targetPixel = getTargetPixel(targetPosition);
    if (targetPixel[3] >= ignoreAlphaThreshold){
      edgePixel = targetPixel;
      edgePixel.w = 0.0;
      edgeIndex = index;
      sum += multiplier;
      if (isEmpty){
        isEmpty = false;
        outputPixel = targetPixel * multiplier;
      } else {
        outputPixel += targetPixel * multiplier;
      }
    } else if (edgeIndex != -1) {
      sum += multiplier;
      outputPixel += edgePixel * multiplier;
    }
  }
}
void main () {
  vec4 o;
  float sum = 0.0;
  bool isEmpty = true;
  #if USE_TEXTURE
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
  #endif
  vec4 edgePixel = vec4(0.5, 0.5, 0.5, 0.0);
  int edgeIndex = -1;
  getNewPixel(0, isEmpty, sum, edgePixel, edgeIndex, o);
  edgePixel = vec4(0.5, 0.5, 0.5, 0.0);
  edgeIndex = -1;
  for (int index = -sampleRate; index < 0; index++){
    getNewPixel(index, isEmpty, sum, edgePixel, edgeIndex, o);
  }
  edgePixel = vec4(0.5, 0.5, 0.5, 0.0);
  edgeIndex = -1;
  for (int index = sampleRate; index > 0; index--){
    getNewPixel(index, isEmpty, sum, edgePixel, edgeIndex, o);
  }
  if (sum > 0.0){
    o /= sum;
  }
  if (isEmpty){
    o = vec4(0.0, 0.0, 0.0, 0.0);
  }
  ALPHA_TEST(o);
  #if USE_BGRA
    gl_FragColor = o.bgra;
  #else
    gl_FragColor = o.rgba;
  #endif
}

